#import "RNSConversions.h"

namespace rnscreens::conversion {

UISplitViewControllerSplitBehavior SplitViewSplitBehaviorFromHostProp(int /* behavior */)
{
  return UISplitViewControllerSplitBehaviorAutomatic;
}

UISplitViewControllerPrimaryEdge SplitViewPrimaryEdgeFromHostProp(int /* primaryEdge */)
{
  return UISplitViewControllerPrimaryEdgeLeading;
}

UISplitViewControllerDisplayMode SplitViewDisplayModeFromHostProp(int /* displayMode */)
{
  return UISplitViewControllerDisplayModeAutomatic;
}

} // namespace rnscreens::conversion
